import { DefaultTheme } from "styled-components";

export const theme: DefaultTheme = {
  color: {
    black: `hsla(0, 0%, 0%, 1)`,
    focus: `hsla(207, 90%, 54%, 1)`,
    white: `hsla(0, 100%, 100%, 1)`
  },
  fontSize: [`16px`, `40px`],
  fontFamily: `'VCR_OSD_MONO', mono-space`,
  spacePx: 4,
}
